import {
    Box,
    Textarea,
    Button,
    HStack,
    Text
} from '@chakra-ui/react';

function CommentBox() {

    return (
        <Box m={20}> 
            <Textarea placeholder='Add your thoughts' />   

            <HStack>
                <Text>Avatar/Username placeholder</Text>
                <Button justifyContent='flex-end' >Comment</Button>
            </HStack>
        </Box>
    )
}

export default CommentBox;
